// Process incoming webhook data
const eventData = $json;

// Log the webhook data for debugging
console.log('Webhook received:', JSON.stringify(eventData, null, 2));

// Extract and structure the data
const processedData = {
  event: eventData.event,
  timestamp: eventData.timestamp || new Date().toISOString(),
  user: eventData.user,
  file: eventData.file || null,
  command: eventData.command || null,
  project: eventData.project || null,
  processed: true,
  workflow_id: 'cursor-webhook-workflow',
  log_level: 'info'
};

// You can add custom logic here based on the event type
if (eventData.event === 'file.save') {
  console.log(`File saved by ${eventData.user}: ${eventData.file?.path}`);
  // Add your custom logic for file save events
} else if (eventData.event === 'file.open') {
  console.log(`File opened by ${eventData.user}: ${eventData.file?.path}`);
  // Add your custom logic for file open events
} else if (eventData.event === 'command.execute') {
  console.log(`Command executed by ${eventData.user}: ${eventData.command?.name}`);
  // Add your custom logic for command execution
}

return processedData;
